mapper.select:
SELECT  boardno, name, rdate, categrpno, memberno
FROM    board
WHERE   boardno = #{boardno}

mapper.update:
UPDATE board 
SET name=#{name}, categrpno=#{categrpno}, memberno=#{memberno}
WHERE boardno=#{boardno}

mapper.select:
SELECT COUNT(*) as cnt
FROM board
WHERE categrpno=#{categrpno}

SELECT COUNT(*) as cnt
FROM contents
WHERE boardno=#{boardno}

SELECT id
FROM member
WHERE memberno=#{memberno}
