mapper.update:
UPDATE contents
SET name=#{name}, content=#{content},
    thumb=#{thumb}, photo=#{photo}, size=#{size}
WHERE contentsno=#{contentsno}

mapper.select:
SELECT COUNT(*) as cnt FROM contents
WHERE categoryno=#{categoryno}
WHERE categoryno=#{categoryno} AND word LIKE '%' || #{word} || '%'

SELECT memberno, contentsno, name, content, views, replies, size, photo, thumb, rdate, boardno, memberno, r FROM ( SELECT memberno, contentsno, name, content, views, replies, size, photo, thumb, rdate, boardno, memberno, rownum as r FROM ( SELECT m.memberno, c.contentsno, c.name, c.content, c.views, c.replies, c.size, c.photo, c.thumb, c.rdate, c.boardno, c.memberno FROM member m, contents c
WHERE m.memberno = c.memberno AND categoryno=#{categoryno}
WHERE m.memberno = c.memberno AND categoryno=#{categoryno} AND word LIKE '%' || #{word} || '%'
ORDER BY c.contentsno ASC ) ) WHERE r >= #{startNum} AND r <= #{endNum}

mapper.update:
UPDATE contents
SET ansnum = ansnum + 1
WHERE grpno = #{grpno} AND ansnum > #{ansnum}
