mapper.select:
SELECT COUNT(id) as cnt
FROM member
WHERE id=#{id}

SELECT memberno, id, name, passwd, phone, email, birth, sex, grade, rdate
FROM member
WHERE memberno = #{memberno}

SELECT memberno, id, name, passwd, phone, email, birth, sex, grade, rdate
FROM member
WHERE id = #{id}

mapper.update:
UPDATE member 
SET name=#{name}, phone=#{phone}, email=#{email}
WHERE memberno=#{memberno}

UPDATE member
SET passwd=#{passwd}
WHERE memberno=#{memberno}

mapper.select:
SELECT COUNT(*) as cnt
FROM member
WHERE id=#{id} AND passwd=#{passwd}
